package day1_Datatypes;



class Hello
{
	
	public void abc()
	{
		System.out.println("Hello abc");
	}
	
	public void xyz()
	{
		System.out.println("Hello xyz");
	}		
}

public class HelloObj {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Hello h1 = new Hello();
		
		h1.abc();
		h1.xyz();
		
		
	}

}
